CREATE TABLE PRODUCT(
	PRODUCT_ID VARCHAR2(20) PRIMARY KEY,
	PRODUCT_NAME VARCHAR2(100) NOT NULL,
	PRODUCT_PRICE NUMBER NOT NULL,
	PRODUCT_MAKER VARCHAR2(50) NOT NULL,
